import React, { useState } from "react";
import Tooltip from '@material-ui/core/Tooltip';
import { withStyles } from '@material-ui/core/styles';

// const LightTooltip = withStyles((theme) => ({
//     tooltip: {
//         backgroundColor: theme.palette.common.white,
//         color: 'black'
//     },
// }))(Tooltip);

const HtmlTooltip = withStyles((theme) => ({
    tooltip: {
        backgroundColor: '#f5f5f9',
        color: 'rgba(0, 0, 0, 0.87)',
        maxWidth: 220,
        fontSize: theme.typography.pxToRem(12),
        border: '1px solid #dadde9',
    },
}))(Tooltip);

function UndoPaste() {

    const [onUndo, setUndoMouse] = useState(false);
    const [onCopyPaste, setCopyPasteMouse] = useState(false);

    function handleMouseOver(event) {
        let id = event.target.id;
        if (id == "undo") {
            setUndoMouse(true);
        } else if (id == "copyPaste") {
            setCopyPasteMouse(true);
        }
    }

    function handleMouseLeave(event) {
        let id = event.target.id;
        if (id == "undo") {
            setUndoMouse(false);
        } else if (id == "copyPaste") {
            setCopyPasteMouse(false);
        }
    }
    return <div>
        <LightTooltip title="Undo" arrow placement="bottom" >
            <div className="bottomObj"
                id="undo"
                style={{ backgroundColor: onUndo && "#EDEBE9" }}
                onMouseOver={handleMouseOver}
                onMouseLeave={handleMouseLeave}>
                <img src="images/undo_icon.png" />
                <img src="images/down-arrow.png" style={{ width: "12px", height: "12px" }} />
            </div>
        </LightTooltip>
        <div className="bottomObj"
            id="copyPaste"
            style={{ backgroundColor: onCopyPaste && "#EDEBE9" }}
            onMouseOver={handleMouseOver}
            onMouseLeave={handleMouseLeave}>
            <img src="images/clipboard.png" />
            <img src="images/down-arrow.png" style={{ width: "12px", height: "12px" }} />
        </div>
    </div>;
}

export default UndoPaste;
